pragma solidity ^0.4.2;

contract DappToken{
	//Constructor
	//Set the tokens
	//Read the total no of tokens

	string public name = "DApp Token";

	string public symbol = "DAPP";

	string public standard = "DApp Token v1.0";

	uint256 public totalSupply;

	event Transfer(
		address indexed _from,
		address indexed _to,
		uint256 _value
	);

	event Approval(
		address indexed _owner,
		address indexed _spender,
		uint256 _value
	);

	mapping(address => uint256)public balanceOf;
	mapping(address => mapping(address => uint256)) public allowance;

	function DappToken (uint256 _initialSupply) public {
		balanceOf[msg.sender] = _initialSupply;
		totalSupply = _initialSupply;
		//allocate the initial supply
	}

	function transfer(address _to, uint256 _value) public returns (bool success) {
		require(balanceOf[msg.sender] >= _value);

		balanceOf[msg.sender] -= _value;

		balanceOf[_to] += _value;

		Transfer(msg.sender, _to, _value);
		return true;
	}

	//APPROVAL

	function approve(address _spender, uint256 _value) public returns (bool success) {

		allowance[msg.sender][_spender] = _value;

		Approval(msg.sender, _spender, _value);

		return true;
	}

	//TRANSFER FROM FUNCTION (THIS FUNCTION IS GOING TO TRANSFER ON OUR BEHALF)

	function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {

		require(_value <= balanceOf[_from]);   // require the from has enough tokens
		require(_value <= allowance[_from][msg.sender]);   // require allowance is big enough 

		// call a transfer event
		Transfer(_from, _to, _value);

		// change the balance 
		balanceOf[_from] -= _value;
		balanceOf[_to] += _value;
		//update the allowance

		allowance[_from][msg.sender] -= _value;

		// return a boolean
		return true;





	}
}
